import React, { Component } from 'react'



export default class comment extends Component {

  urlRegex = /(([a-z]+:\/\/)?(([a-z0-9\-]+\.)+([a-z]{2}|aero|arpa|biz|com|coop|edu|gov|info|int|jobs|mil|museum|name|nato|net|org|pro|travel|local|internal))(:[0-9]{1,5})?(\/[a-z0-9_\-\.~]+)*(\/([a-z0-9_\-\.]*)(\?[a-z0-9+_\-\.%=&amp;]*)?)?(#[a-zA-Z0-9!$&'()*+.=-_~:@/?]*)?)(\s+|$)/gi


  links = this.props.data.content.replace(this.urlRegex, function(url) {
    return <a href="' + url + '">' + url + '</a>;
})

  render() {

    return (
      <div className='article-comment'>
        <img src={this.props.data.user.profileImg} height='100px' alt="Users profile img" />
        <div className='article-comment-content'>
          <h3>{this.props.data.username}</h3>
          <p>{this.links}</p>
        </div>
      </div>
    )
  }
}
